                "value": 65536
            },
            {
                "domain": "root",
                "limit_type": "hard",
                "limit_item": "nofile",
                "value": 65536
            },
            {
                "domain": "root",
                "limit_type": "soft",
                "limit_item": "nproc",
                "value": "unlimited"
            },
            {
                "domain": "root",
                "limit_type": "hard",
                "limit_item": "nproc",
                "value": "unlimited"
            }
        ]
    }
